Datasource.Parameter:
(select geometry, type
from osm_waterways
) as waterway

(select geometry, type
from osm_transport_areas
) as transport

(select geometry, type from osm_roads_gen1
where type in ('motorway', 'motorway_link', 'trunk', 'trunk_link', 'primary')
) as road

(select geometry, type, tunnel from osm_roads_gen1
where type in ('motorway', 'motorway_link', 'trunk', 'trunk_link', 'primary')
) as roads

(select geometry, type, name from osm_roads
where name is not null and  type in ('living_street','residential','road','service','unclassified','unknown')
) as label
